
public class Q16 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int b=2;
		int a=10;
		int c=10;
		int d=2;
		int e=10;
		int f=2;
		int g=10;
		int h=2;
		c=++b;
		a=d++;
		e=--f;
		g=h--;
		System.out.println("a: "+a+" b: "+b+" c: "+c+" d: "+d);
		System.out.println("e: "+e+" f: "+f+" g: "+g+" h: "+h);
	}

}
